 /*global window, document, rJS, URI, RSVP */
 (function (window, document, rJS, URI, RSVP) {
 "use strict";

 rJS(window)
 /////////////////////////////////////////////////////////////////
 // ready
 /////////////////////////////////////////////////////////////////
 // Init local properties
 .ready(function (g) {
 g.props = {};

 })

 // Assign the element to a variable
 .ready(function (g) {
 return g.getElement()
 .push(function (element) {
 g.props.element = element;
 });
 })

 //////////////////////////////////////////////
 // acquired method
 //////////////////////////////////////////////
 .declareAcquiredMethod("jio_allDocs", "jio_allDocs")
 .declareAcquiredMethod("translateHtml", "translateHtml")
 .declareAcquiredMethod("whoWantToDisplayThis", "whoWantToDisplayThis")
 .declareAcquiredMethod("pleasePublishMyState", "pleasePublishMyState")
 //////////////////////////////////////////////
 // initialize the gadget content
 //////////////////////////////////////////////
 .declareMethod('render', function (options) {
 var gadget = this,
 thead = gadget.props.element.querySelector('thead'),
 field_json = options.field_json,
 tr = document.createElement("tr"),
 th,
 i;

 gadget.props.field_json = field_json;
 gadget.props.begin_from = parseInt(options.begin_from, 10) || 0;
 gadget.props.extended_search = options.extended_search;

 for (i = 0; i < field_json.column_list.length; i += 1) {
 th = document.createElement("th");
 th.textContent = field_json.column_list[i][1];
 th.setAttribute("data-i18n", field_json.column_list[i][1]);
 tr.appendChild(th);
 }
 thead.appendChild(tr);
 })

 //////////////////////////////////////////////
 // render the listbox in an asynchronous way
 //////////////////////////////////////////////
 .declareService(function () {
 var gadget = this,
 props = gadget.props,
 field_json = props.field_json,
 begin_from = props.begin_from,
 table = props.element.querySelector('table'),
 original_tfoot = props.element.querySelector('tfoot'),
 tbody = document.createElement("tbody"),
 url_query = props.extended_search,
 query_string = "",
 lines = field_json.lines,
 select_list = [],
 dataset,
 counter,
 i;

 if (field_json.query === undefined) {
 original_tfoot.textContent = "Unsupported list method: '" + field_json.list_method + "'";
 return;
 }
 // function buildQueryString(previous, next) {
 // return previous + next[0] + ':= "' + url_query + '" OR ';
 // }

 if (url_query) {
 //query_string = field_json.column_list.reduce(buildQueryString, ' AND (').replace(new RegExp("OR " + '$'), ')');
 query_string = ' AND ' + url_query;
 }

 for (i = 0; i < field_json.column_list.length; i += 1) {
 select_list.push(field_json.column_list[i][0]);
 }

 return gadget.jio_allDocs({
 "query": new URI(field_json.query).query(true).query + query_string,
 "limit": [begin_from, begin_from + lines + 1],
 "select_list": select_list
 }).push(function (result) {
 var promise_list = [result];
 counter = Math.min(result.data.total_rows, lines);
 for (i = 0; i < counter; i += 1) {
 promise_list.push(
 gadget.whoWantToDisplayThis(result.data.rows[i].id)
 );
 }
 return RSVP.all(promise_list);

 }).push(function (result_list) {
 var j,
 tr,
 th,
 a,
 result = result_list[0],
 tmp_url;

 dataset = result;

 for (i = 0; i < counter; i += 1) {
 tr = document.createElement("tr");
 tmp_url = result_list[i + 1];

 for (j = 0; j < field_json.column_list.length; j += 1) {
 th = document.createElement("th");
 a = document.createElement("a");
 a.href = tmp_url;
 a.className = "ui-link";
 a.textContent = (result.data.rows[i]
 .value[field_json.column_list[j][0]] || "");
 th.appendChild(a);
 tr.appendChild(th);
 }
 tbody.appendChild(tr);
 }

 }).push(function () {
 var prev_param = {},
 next_param = {};

 function setNext() {
 if (dataset.data.rows.length > lines) {
 next_param.begin_from = begin_from + lines;
 }
 }

 if (begin_from === 0) {
 setNext();
 } else {
 prev_param.begin_from = begin_from - lines;
 setNext();
 }

 return RSVP.all([
 gadget.pleasePublishMyState(prev_param),
 gadget.pleasePublishMyState(next_param)
 ]);

 }).push(function (url_list) {
 var fragment = document.createDocumentFragment(),
 tfoot = gadget.__element.querySelector("tfoot"),
 //tfoot = document.createElement("tfoot"),
 tr = document.createElement("tr"),
 th = document.createElement("th"),
 paging_prev = document.createElement("a"),
 paging_next = document.createElement("a"),
 paging_info = document.createElement("span"),
 paging_menu = document.createElement("div"),
 paging_wrap = document.createElement("div"),
 temp = document.createElement("div"),
 temp_table = document.createElement("table"),
 len;

 th.setAttribute("colspan", field_json.column_list.length);
 // NOTE: Romain: paging menu with 3+ buttons should have another class name (like ui-paging-grid)
 paging_menu.className = "ui-controlgroup ui-controlgroup-horizontal ui-corner-all ui-paging-menu";
 paging_wrap.className = "ui-controlgroup-controls";

 paging_prev.className = "ui-btn ui-icon-carat-l ui-btn-icon-left responsive ui-first-child";
 paging_prev.textContent = "Previous";
 paging_prev.setAttribute("data-i18n", "previous");
 paging_prev.href = url_list[0];
 paging_info.className = "ui-btn ui-disabled";
 if ((begin_from === 0) && (counter === 0)) {
 paging_info.textContent = "No records";
 paging_info.setAttribute("data-i18n", "No Records");
 } else if ((dataset.data.rows.length <= lines) && (begin_from === 0)) {
 paging_info.setAttribute("data-i18n", "[last]Record(s)");
 paging_info.appendChild(document.createTextNode(counter));
 paging_info.appendChild(document.createTextNode("Record(s)"));
 } else {
 paging_info.setAttribute("data-i18n", "[node]Record(s)");
 paging_info.appendChild(document.createTextNode("Records "));
 paging_info.appendChild(document.createTextNode((((begin_from + lines) / lines - 1) * lines + 1) + " - " + (((begin_from + lines) / lines - 1) * lines + counter)));
 }
 // paging_info.textContent = "Page " + ((begin_from + lines) / lines);
 paging_next.className = "ui-btn ui-icon-carat-r ui-btn-icon-right responsive ui-last-child";
 paging_next.textContent = "Next";
 paging_next.setAttribute("data-i18n", "next");
 paging_next.href = url_list[1];

 if (begin_from === 0) {
 paging_prev.className += " ui-disabled";
 }
 if (dataset.data.rows.length <= lines) {
 paging_next.className += " ui-disabled";
 }

 paging_wrap.appendChild(paging_prev);
 paging_wrap.appendChild(paging_info);
 paging_wrap.appendChild(paging_next);
 paging_menu.appendChild(paging_wrap);
 th.appendChild(paging_menu);
 tr.appendChild(th);
 tfoot.appendChild(tr);
 fragment.appendChild(tbody);
 fragment.appendChild(tfoot);

 for (len = table.children.length; len > 0; len -= 1) {
 table.removeChild(table.lastChild);
 }

 // NOTE: for passing translations, temp must contain a table
 temp_table.appendChild(fragment);
 temp.appendChild(temp_table);

 return gadget.translateHtml(temp.innerHTML);
 })
 .push(function (my_translated_html) {
 var temp = document.createElement("div");

 temp.innerHTML = my_translated_html;
 gadget.props.element.appendChild(temp.firstElementChild);
 return gadget;
 });
 });

 }(window, document, rJS, URI, RSVP));
